/*------------------------------------------------------------------
	UTILITY FUNCTIONS
-------------------------------------------------------------------*/

(function($,sr){

	// debouncing function from John Hann
	// http://unscriptable.com/index.php/2009/03/20/debouncing-javascript-methods/

	var debounce = function (func, threshold, execAsap) {

		var timeout;

		return function debounced () {

			var obj = this, args = arguments;
			function delayed () {
				if (!execAsap)
					func.apply(obj, args);
				timeout = null;
			};

			if (timeout)
				clearTimeout(timeout);
			else if (execAsap)
				func.apply(obj, args);

			timeout = setTimeout(delayed, threshold || 150);

		};
	}

	jQuery.fn[sr] = function(fn){  return fn ? this.bind('resize', debounce(fn)) : this.trigger(sr); };

})(jQuery,'smartresize');

/* **********************************************
     Begin _ui.js
********************************************** */

/*------------------------------------------------------------------
	USER INTERFACE COMPONENTS
-------------------------------------------------------------------*/

(function() {

	var $w = $(window),
		sw = document.documentElement.clientWidth,
		sh = document.documentElement.clientHeight,
		navBreak = 1024;

		$w.smartresize(function(){
			sw = document.documentElement.clientWidth;
			sh = document.documentElement.clientHeight;
		});

		return uiFunctions = {

			site: {

				setFooterYear: function() {
					var year = new Date().getFullYear();
					$('#footer__year').text(year);
				},

				unsetColumnHeight: function(selectorArray) {

					$(document).ready(function() {

						var selectors = new Array();

						$.each(selectorArray, function(index, value){
							selectors.push($(value));
						});

						$.each(selectors, function() {
							$(this).css('height', '');
						});

					});

				},

				setColumnHeight: function(selectorArray) {

					$(document).ready(function() {

						var selectors = new Array(),
							currentTallest = 0,
							currentRowStart = 0,
							rowDivs = new Array(),
							$el,
							topPosition = 0;

						$.each(selectorArray, function(index, value){
							selectors.push($(value));
						});

						function setHeights() {

							$.each(selectors, function() {

								$el = $(this);
								topPostion = $el.position().top;

								if (currentRowStart != topPostion) {

									for (currentDiv = 0 ; currentDiv < rowDivs.length ; currentDiv++) {
										rowDivs[currentDiv].height(currentTallest);
									}

									rowDivs.length = 0;
									currentRowStart = topPostion;
									currentTallest = $el.height();
									rowDivs.push($el);

								} else {
									rowDivs.push($el);
									currentTallest = (currentTallest < $el.height()) ? ($el.height()) : (currentTallest);
								}

								for (currentDiv = 0 ; currentDiv < rowDivs.length ; currentDiv++) {
									rowDivs[currentDiv].height(currentTallest);
								}

							});
						}

						setHeights();

					});

				},

				setupScrollToLinks: function() {
					$('.scrollto').on('click', function(e) {
						var targetElement = $('#' + $(this).attr('href').substring(1));
						if (targetElement.length >= 1) {
							var target = targetElement.offset().top,
								scrollSpeed = function() { return Math.ceil( Math.abs( target - $w.scrollTop() ) / 1.5 ); };
							$('html, body').animate({
								scrollTop: target
							}, scrollSpeed() );
						}
						e.preventDefault();
					});
				},

				setHeaderWaypoint: function() {

					$('body').waypoint(function(direction){

						if ( direction === 'down' ) {
							$('body').addClass('header-collapse');
						}
						else if ( direction === 'up' ){
							$('body').removeClass('header-collapse');
						}

					}, { offset: -215 } );

				},

				bindScrollToTop: function() {

					$(document).ready(function() {
						$('.primary-nav__scroll-top').on('click', function(e){
							$('html, body').animate({
								scrollTop: 0
							}, 1250);
							e.preventDefault();
						});
					});

				},

				setupMobileMenu: function() {

					var menuStatus = 'closed',
						$nav = $('.mobile-menu #nav'),
						$subMenus = $nav.find('.submenu'),
						navWidth = $nav.innerWidth(),
						navHeight = $nav.innerHeight();

					// Menu toggle

					$('.mobile-menu #nav-toggle').on('click', function(e) {
						if ( menuStatus == 'closed' ) {
							$(this).parents('.mobile-menu').addClass('mobile-menu--open');
							menuStatus = 'open';
							navHeight = $nav.innerHeight();
							$subMenus.each(function() {
								$(this).css({
									'height': navHeight
								});
							});
						}
						else if ( menuStatus == 'open' ) {
							$(this).parents('.mobile-menu').removeClass('mobile-menu--open');
							menuStatus = 'closed';
						}
						e.preventDefault();
					});

					// Append all links to the same list

					$('.mobile-menu .primary-nav__explore li').each(function() {
						$(this).appendTo('.mobile-menu .primary-nav__shop');
					});
					$('.mobile-menu .primary-nav__utility li').each(function() {
						var navItem = $(this),
							navText = $(this).find('.mls').text(),
							navLink = $(this).find('a').attr('href');

						$('.mobile-menu .primary-nav__shop').append('<li><a href="' + navLink + '">' + navText + '</a></li>');

						// $(this).appendTo('.mobile-menu .primary-nav__shop');
					});

					// Submenus

					$('.mobile-menu .parentmenu li a').on('click', function(e) {
						if ( $(this).next('ul.submenu').length > 0 ) {
							$(this).next('.submenu').addClass('submenu--open');
							e.preventDefault();
						}
					});

					$('.mobile-menu .submenu').each(function() {

						var width = $(this).innerWidth(),
							subMenu = $(this);

						subMenu.prepend('<li class="submenu__back"><a href="#">&lt; Back</a></li>');
						subMenu.css({
							'right': -width
						});

						$(this).find('.submenu__back').on('click', function(e) {
							subMenu.removeClass('submenu--open');
							e.preventDefault();
						});

					});

				},

				setupHeader: function() {

					$(document).ready(function() {

						if ( $('body').find('.header--clone').length == 0 ) {

							// Create a clone of the header (for desktop fixed + mobile menu)
							$('.header-container').clone().insertAfter('.header-container').addClass('header--clone');

							// Bind "scroll to top" event listener
							uiFunctions.site.bindScrollToTop();

						}

						// Setup header + nav for small screens
						if ( sw < navBreak ) {
							$('.header-container').not( $('.header--clone') ).hide();
							$('.header--clone').addClass('mobile-menu');
							uiFunctions.site.setupMobileMenu();
						}
						else if ( sw >= navBreak ) {
							$('.header-container').not( $('.header--clone') ).show();
							$('.header--clone').removeClass('mobile-menu');
						}

					});

				},

				bindLoginPageControls: function() {

					$('#login__current-user__btn, #login__create-acct__btn').on('click', function(e) {
						$(this).hide().next('form').fadeIn();
						e.preventDefault();
					});

				}

			}
		}

})();

/* **********************************************
     Begin _init.js
********************************************** */

/*------------------------------------------------------------------
    INITIALIZERS: UI components
-------------------------------------------------------------------*/

document.addEventListener("touchstart", function(){}, true);

var functionCheck = jRespond([
		{
			label: 'med-lrg',
			enter: 648,
			exit: 10000
		}
	]);

	functionCheck.addFunc({
		breakpoint: '*',
		enter: function() {
			uiFunctions.site.setupHeader();
			uiFunctions.site.setFooterYear();
			uiFunctions.site.bindLoginPageControls();
			uiFunctions.site.setHeaderWaypoint();
			uiFunctions.site.setupScrollToLinks();

			var mantleSlider = $('.bxslider').bxSlider({
				adaptiveHeight: true,
				slideWidth: 1200,
				onSliderLoad: function(currentSlide, currentIndex) {
					$(currentSlide).addClass('active');
				},
				onSlideNext: function($slideElement, oldIndex, newIndex) {
					$slideElement.addClass('active').siblings().removeClass('active');
				},
				onSlidePrev: function($slideElement, oldIndex, newIndex) {
					$slideElement.addClass('active').siblings().removeClass('active');
				}
			});

		}
	});

	functionCheck.addFunc({
		breakpoint: 'med-lrg',
		enter: function() {

			if ( $('body').hasClass('index') ) {
				uiFunctions.site.setColumnHeight(['.featured-products__grid li', '.featured-recipes__grid ul li']);
			}
			if ( $('body').hasClass('product-single') ) {
				uiFunctions.site.setColumnHeight(['.related-products__grid ul li']);
			}
			if ( $('body').hasClass('page__recipes__category') ) {
				uiFunctions.site.setColumnHeight(['.recipes__category__list li']);
			}
			if ( $('body').hasClass('page__shop') ||  $('body').hasClass('page__shop-option02') ) {
				uiFunctions.site.setColumnHeight(['.shop-products__grid li']);
			}

			uiFunctions.site.setColumnHeight(['.footer__primary > .row > div']);

		},

		exit: function() {

			if ( $('body').hasClass('index') ) {
				uiFunctions.site.unsetColumnHeight(['.featured-products__grid li', '.featured-recipes__grid ul li']);
			}
			if ( $('body').hasClass('product-single') ) {
				uiFunctions.site.unsetColumnHeight(['.related-products__grid ul li']);
			}
			if ( $('body').hasClass('page__shop') ) {
				uiFunctions.site.unsetColumnHeight(['.shop-products__grid li']);
			}

		}
	});